import React from "react";
import {
  Box,
  Button,
  Divider,
  Flex,
  Heading,
  HStack,
  Image,
  Radio,
  RadioGroup,
  Select,
  Stack,
  Text,
} from "@chakra-ui/react";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { deletecartitems, getcartitems } from "../../Redux/cart/cart.actions";
import { GiConsoleController } from "react-icons/gi";
import CartItem from "./CartItem";
import { useState } from "react";
import { Link } from "react-router-dom";
import OrderSummary from "./OrderSummary";
import Loading from "../Loading";

const Cart = () => {
  const data = useSelector((store) => store.cartManager.data);
  const price = useSelector((store) => store.cartManager.price);
  const dispatch = useDispatch();
  console.log(data);

  const handleRemove = (id) => {
    dispatch(deletecartitems(id));
  };

  useEffect(() => {
    dispatch(getcartitems());
  }, []);

  if (data.length == 0) {
    return <Loading />;
  } else {
    return (
      <Box maxW="1250px" m="60px auto" fontFamily="'Visuelt Pro', sans-serif">
        <HStack>
          <Heading
            color="#1e3352"
            textAlign="left"
            size="md"
            fontSize="24px"
            letterSpacing="1.5px"
          >
            Shopping Bag
          </Heading>
          <Text>{data.length} Items</Text>
        </HStack>
        <HStack
          alignItems="flex-start"
          justifyContent="space-between"
          flexWrap="wrap"
        >
          <Box>
            {data.map((item) => {
              return <CartItem {...item} handleRemove={handleRemove}/>;
            })}
          </Box>
          <Box bgColor="#f7f7f7" w="360px" p="20px" lineHeight="10">
            <OrderSummary />
            <Button
              bgColor="#1e3352"
              color="white"
              fontWeight="normal"
              _hover={{ bgColor: "black" }}
              w="100%"
              borderRadius="0px"
              mt="20px"
              p="25px"
            >
              <Link to="/shipping">CHECKOUT</Link>
            </Button>
            <Box
              w="250px"
              h="35px"
              bgColor="#ffc439"
              borderRadius="20px"
              m="20px auto"
            >
              <HStack justify="center" alignItems="center" h="100%">
                <Image
                  w="5%"
                  src="data:image/svg+xml;base64,"
                />
                <Image
                  w="20%"
                  src="data:image/svg+xml;base64,"
                />
                <Text p="0px" m="0px">
                  Checkout
                </Text>
              </HStack>
            </Box>
            <Heading size="sm" mb="10px" color="#1e3352" textAlign="left">
              Estimated Shipping Cost
            </Heading>
            <RadioGroup
              mt="20px"
              textAlign="left"
              color="#6d7278"
              lineHeight="25px"
              value="1"
            >
              <Stack gap="20px">
                <Radio value="1">
                  Economy Ground (Delivered In 3 - 7 Business Days.): $5.00
                </Radio>
                <Radio>
                  Standard Ground (Delivered In 3 - 5 Business Days.): $8.00
                </Radio>
                <Radio>
                  2 Business Days (Order By 1:30PM EST, No Weekend Deliveries):
                  $13.00
                </Radio>
                <Radio>
                  Overnight (Order By 1:30pm EST, No Weekend Deliveries): $25.00
                </Radio>
              </Stack>
            </RadioGroup>
          </Box>
        </HStack>
      </Box>
    );
  }
};

export default Cart;
